    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
            font-size: 16px;
            background-color: #fff;
            overflow: hidden;
        }

        h1 {
            color: #323330;
        }

        .container {
            height: 100vh;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            margin: 20px;

        }

        .drop-targets {
            display: flex;
            flex-direction: row;
            justify-content: space-around;
            align-items: center;

            margin: 20px 0;
        }

        .box {
            height: 150px;
            width: 150px;
            border: solid 3px #ccc;
            margin: 10px;

            /* align items in the box */
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;

        }


        .drag-over {
            border: dashed 3px red;
        }

        .item {
            height: 75px;
            width: 75px;
            background-color: #F0DB4F;

        }

        .hide {
            display: none;
        }
    </style>
